mapper.update:
/* updateFailCntReset */
update member
   set login_fail_cnt = 0
 where member_no = #{memberNo}

/* updateFailCntAdd */
update member
   set login_fail_cnt = (login_fail_cnt + 1)
 where member_no = #{memberNo}

/* updateFailCntAdd */
update member
   set lock_yn = 'Y'
 where member_no = #{memberNo}

/* updateFailCntAdd */
update member
   set lock_yn = 'Y'
     , lobk_time = '10'
 where member_no = #{memberNo}
